import "./raiting.css"

const Rating = ({ rating }: { rating: number }) => {
    rating = Math.floor(rating * 10) / 10;
    let color: string;
    if (rating < 4.3) {
        color = '#C82020'
    } else if (rating < 6.4) {
        color = '#777777'
    } else if (rating < 7.6) {
        color = '#308E21'
    } else {
        color = '#A59400'
    }

    return <div className='raiting' style={{ backgroundColor: color }}>
        <svg width="16" height="15" viewBox="0 0 16 15" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M8.00105 12.1734L3.29875 14.8055L4.34897 9.51997L0.392578 5.86124L5.74394 5.22675L8.00105 0.333374L10.2581 5.22675L15.6095 5.86124L11.6531 9.51997L12.7033 14.8055L8.00105 12.1734Z" fill="white" />
        </svg>
        <span>{rating}</span>
    </div>
}
export default Rating;
